package JavaClass;

public class ControlStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20, b = 100, c = 15;
		if (a > b && a > c) // relational operator
			System.out.println("a");
		else if (b > c)
			System.out.println("b");
		else
			System.out.println("c");

	}

}
